            this.maxLength = parseInt(this.getAttrFromNodeXml(nodeXml,"maxlength"));
        if (this.getAttrFromNodeXml(nodeXml,"text") !== null)
            this.text = this.getAttrFromNodeXml(nodeXml,"text");
